sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	// formats the subject of new reservation emails
	var date = new GlideDateTime(current.start);
	var day = date.getDayOfMonthLocalTime();
	var dayOW = date.getDayOfWeekLocalTime();
	var month = date.getMonthLocalTime();
	var timeUnsplit = date.getLocalTime() + ""; //returns 1970-01-01 <time we want>
	timeUnsplit = timeUnsplit.split(" ")[1]; //remove the date
	var time = timeUnsplit.split(":"); //now give us hours, mins, and seconds in an array


	//format date for display as a string
	if(dayOW == 1)
		dayOW = "Monday";
	else if(dayOW == 2) 
		dayOW = "Tuesday";
	else if(dayOW == 3)
		dayOW = "Wednesday";
	else if(dayOW == 4)
		dayOW = "Thursday";
	else if(dayOW == 5)
		dayOW = "Friday";
	else if(dayOW == 6)
		dayOW = "Saturday";
	else if(dayOW == 7)
		dayOW = "Sunday";

	if(month == 1)
		month = 'January';
	else if(month == 2)
		month = 'February';
	else if(month == 3)
		month = 'March';
	else if(month == 4)
		month = 'April';
	else if(month == 5)
		month = 'May';
	else if(month == 6)
		month = 'June';
	else if(month == 7)
		month = 'July';
	else if(month == 8)
		month = 'August';
	else if(month == 9)
		month = 'September';
	else if(month == 10)
		month = 'October';
	else if(month == 11)
		month = 'November';
	else if(month == 12)
		month = 'December';

	//format time in 12-hr
	var hour = time[0];
	var min = time[1];
	var ampm;

	if(hour > 11) {
		ampm = 'PM';
	} else {
		ampm = 'AM';
	}

    if (hour > 12) {
		hour -= 12;
	}   

	time = hour + ":" + min + " " + ampm;

	// display the feature and the time
	var str = "Below are the details for your reservation\n";
	str += "<b>Topic:</b> " + current.feature.name + "\n";
	str += "<b>Time:</b> " + dayOW + ", " + month + " " + day + " at " + time;

	template.print(str);

})(current, template, email, email_action, event);
