sys_ui_script.script:
var reserveApp = angular.module('reserveApp', []);

reserveApp.controller('ReserveCtrl',[
"$scope",
"$http",
function($scope, $http) {
	$scope.url = '/api/now/table/x_casus_reservatio_feature';
	$http.defaults.headers.common.Accept = "application/json";
	$scope.idSelectedVote = "5";
	$scope.setSelected = function (idSelectedVote) {
		$scope.idSelectedVote = idSelectedVote;
		console.log("setting selected to " + idSelectedVote );
	};
	$scope.updateRecordList = function() {

		$http({
			method: 'GET',
			url: $scope.url + "?sysparm_query=typeSTARTSWITHsubject"+
			"^ORDERBYDESCnumber"+
			"&sysparm_limit=25",
		}).
		success( function(data, status) {
			$scope.rfeature = data.result;
		}).
		error ( function(data, status) {
			$scope.rfeature = [{"name": "Error fetching list"}];
		});
	};

	$scope.updateRecordList();

}

] );
